mapper.select:
select count(*) from product

	where pname like #{keyword}


	where pcategory like #{keyword}

		select * from product 

			where pname like #{keyword}


			where pcategory like #{keyword}


        	order by price asc


	        order by price desc


	        order by pnum desc

mapper.update:
update product
set pname=#{pname}, pcategory=#{pcategory}, publisher=#{publisher}, pimage=#{pimage}, pqty=#{pqty}, price=#{price}, summary=#{summary}, point=#{point}
where pnum=#{pnum}
