mapper.select:
select count(*) from product

	where pname like #{keyword}


	where pcategory like #{keyword}

		select * from product 

			where pname like #{keyword}


			where pcategory like #{keyword}


        	order by price asc


	        order by price desc


	        order by pnum desc

mapper.update:
update product
set pname=#{pname}, pcategory=#{pcategory}, publisher=#{publisher}, pimage=#{pimage}, pqty=#{pqty}, price=#{price}, summary=#{summary}, point=#{point}
where pnum=#{pnum}

update product set
pqty = pqty - #{pop_out}
where pname = #{pname}

update product set
pqty = pqty + #{pop_out}
where pname = #{pname}

update product set
pqty = pqty - #{pop_out}
where pname = #{pname}

update product set
pqty = pqty + #{pop_out}
where pname = #{pname}

update product set
orderCount = orderCount + 1
where pname = #{singlePname}

update product set
orderCount = orderCount + 1
where pname = #{pname}
